import { HttpClient } from '@angular/common/http';
import { Attribute, Injectable } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { Card } from '../models/card';

@Injectable({
  providedIn: 'root'
})
export class FutApiService {

  private apiUrl = 'http://localhost:3000/card';

  constructor(
    private http: HttpClient
  ) { }

  const card: Card = {
    id: 1,
    name: 'Carta 1',
    description: 'Descrição da carta 1',
    imageUrl: 'https://example.com/carta1.png'
  };

  getAllCards(): Observable<Card[]> {
    return this.http.get<Card[]>(this.apiUrl);
  }

  getCard(url: string): Observable<Card[]> {
    return this.http.get<Card[]>(url);
}

public addCard(card: Card): Observable<Card[]>{
  return this.http.post<Card[]>(this.apiUrl, card);
}

