import { Component, OnInit } from '@angular/core';
import { Card } from 'src/app/models/card';
import { FutApiService } from 'src/app/service/fut-api.service';

@Component({
  selector: 'app-fut-list',
  templateUrl: './fut-list.component.html',
  styleUrls: ['./fut-list.component.scss']
})
export class FutListComponent implements OnInit{

  public cards: Card[] = [];

  constructor(private futApiService: FutApiService){

  }
  ngOnInit() {
    this.futApiService.getCards().subscribe(res => {
      this.cards = res;
    });

  }

  public getSearch(value: string){
    console.log(value)
  }
}
