import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { forkJoin } from 'rxjs';
import { FutApiService } from 'src/app/service/fut-api.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss'],
})
export class DetailsComponent implements OnInit {

  private urlCard: string = 'http://localhost:3000/card';
  public card: any;



  constructor(
    private activatedRoute: ActivatedRoute,
    private futApiService: FutApiService
  ) {}

  ngOnInit(): void {
    this.activatedRoute.params.subscribe((params) => {
      const id = params['id'];
      this.futApiService.getCard(`${this.urlCard}/${id}`).subscribe((res) => {
        this.card = res;
        console.log(res);
      });
    });
  }



}


<app-fut-header></app-fut-header>

<main>
    <div class="card-details">
        <app-fut-card-details></app-fut-card-details>
        <div class="card-details__content">
            <h2>Detalhes do jogador</h2>
            <p>Nome: {{ card?.firstName }} {{ card?.lastName }}</p>

            <!-- <p>Clube: {{ card?.club }}</p>  -->

        </div>
      </div>
</main>
