import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-fut-card-details',
  templateUrl: './fut-card-details.component.html',
  styleUrls: ['./fut-card-details.component.scss']
})
export class FutCardDetailsComponent {

  @Input() card: any;

  constructor() {}


  public logoMapping: { [key: string]: string } = {
    'psg': 'assets/images/clubs/psg-logo.png',
    // 'barcelona': 'https://www.example.com/logo-barcelona.png',
    // 'real_madrid': 'https://www.example.com/logo-real-madrid.png'
  };

  public nationMapping: { [key: string]: string } = {
    'brazil': 'assets/images/nation/brazil.png',
    'argentina': 'assets/images/nation/argentina.png',
    'france': 'assets/images/nation/france.png',
  };

  public cardGoldMapping: { [key: string]: string } = {
    'fifa-16': 'assets/images/cards/card-fifa-16.png',
    'fifa-17': 'assets/images/cards/card-fifa-17.png',
    'fifa-18': 'assets/images/cards/card-fifa-18.png',
    'fifa-19': 'assets/images/cards/card-fifa-19.png',
  };

}
